import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate,
  useLocation,
} from "react-router-dom";
import { supabase } from "./supabaseClient";
import Login from "./Login";
import Navbar from "./navbar";
import LiveLoads from "./LiveLoads";
import CustomerProspects from "./CustomerProspects";
import HomePage from "./HomePage";
import LearnToPlay from "./LearnToPlay";
import ResetPassword from "./ResetPassword";

const App = () => {
  const [session, setSession] = useState(null);
  const [isResettingPassword, setIsResettingPassword] = useState(false);

  useEffect(() => {
    supabase.auth.getSession().then(({ data: { session } }) => {
      setSession(session);
    });

    const {
      data: { subscription },
    } = supabase.auth.onAuthStateChange((_event, session) => {
      setSession(session);
    });

    return () => subscription.unsubscribe();
  }, []);

  const MainContent = () => {
    const location = useLocation();

    useEffect(() => {
      const hash = location.hash;
      setIsResettingPassword(hash && hash.includes("type=recovery"));
    }, [location]);

    return (
      <>
        <Navbar session={session} />
        <Routes>
          <Route
            path="/login"
            element={
              !session && !isResettingPassword ? <Login /> : <Navigate to="/" />
            }
          />
          <Route path="/" element={<HomePage />} />
          <Route path="/reset-password" element={<ResetPassword />} />
          <Route
            path="/live-load-board"
            element={
              session && !isResettingPassword ? (
                <LiveLoads />
              ) : (
                <Navigate to="/" />
              )
            }
          />
          <Route
            path="/customer-prospects"
            element={
              session && !isResettingPassword ? (
                <CustomerProspects />
              ) : (
                <Navigate to="/login" />
              )
            }
          />
          <Route path="/learn-to-play" element={<LearnToPlay />} />
          {isResettingPassword && (
            <Route path="*" element={<ResetPassword />} />
          )}
        </Routes>
      </>
    );
  };

  return (
    <Router>
      <MainContent />
    </Router>
  );
};

export default App;
